const dropdownFruit = () => {
  const fruit = document.getElementById("DropdownFruit");
  console.log(fruit);
  // const fruitName = fruit.options[fruit.selectedIndex].value;
  // console.log(fruitName);
  fruit.addEventListener("click", (event) => {
    console.log('fruit');
    console.log(event); // toElement: a -> text: "framboise"
    console.log(event.currentTarget);
    alert(event.target.nodeName);
  });
};

const dropdownCategory = () => {
  const category = document.getElementById("DropdownCategory");
  category.addEventListener("click", (event) => {
    console.log('cat');
    console.log(event);
    console.log(event.currentTarget);
  });
};

const dropdownType = () => {
  const type = document.getElementById("DropdownType");
  type.addEventListener("click", (event) => {
    console.log('type');
    console.log(event);
    console.log(event.currentTarget);
  });
};

export { dropdownFruit } ;
export { dropdownCategory } ;
export { dropdownType } ;
